mapper.select:
 select * from 
		(select rownum as rnum,a.* from 
		(select notice.*,admin.a_name from admin,notice where admin.a_idx=notice.a_idx order by notice_idx desc)a)b 
		where rnum>=#{start} 
		and rnum<=#{end}
		and notice_viewer = '전체'

