mapper.update:
 update 
	notice 
 set
 	notice_subj=#{notice_subj},
 	notice_viewer=#{notice_viewer},
 	notice_cont=#{notice_cont}
 where 
 	notice_idx= #{notice_idx}
 and
 	a_idx= #{a_idx}
